mapper.select:
select user.id userId, user.portrait userPortraitUrl, user.nickname userName,
account.id roomId, account.live_path livePath, account.online_number onlineNumber, account.cover_url coverUrl,
info.id infoId, info.title title, info.introduction introduction from live_account account inner join user on account.user_id = user.id inner join live_info info on user.id = info.user_id where account.state = 1 and info.is_deleted = 0 order by onlineNumber DESC LIMIT 5;

select user.id userId, user.portrait userPortraitUrl, user.nickname userName,
account.id roomId, account.live_path livePath, account.online_number onlineNumber, account.cover_url coverUrl,
info.id infoId, info.title title, info.introduction introduction, info.type_id typeId from live_account account inner join user on account.user_id = user.id inner join live_info info on user.id = info.user_id where info.is_deleted = 0 and account.user_id = #{ userId }
